//import './NewItem.css';

export default function NewItem({}){
    var name;
    var quantity;
    var price;
    const nameChange = (event) => {
        name = event.target.value;
        console.log("name: " + name);
    };
    const quantityChange = (event) => {
        quantity = event.target.value;
        console.log("quantity: " + quantity);
    };
    const priceChange = (event) => {
        price = event.target.value;
        console.log("price: " + price);
    };

    return (
        <div>
            <link rel="stylesheet" href="./NewStore.css"/>
            <h1>New Item Form</h1>
            <form id="todoForm" onSubmit={ () => handleSubmit({name}) }>
                <div id="storeNew">
                    Item Name: 
                    <input type="text" name="name" onInput={nameChange}></input>
                </div>
                <div id="storeNew">
                    Item Price: 
                    <input type="text" name="price" onInput={priceChange}></input>
                </div>
                <div id="storeNew">
                    Item Quantity: 
                    <input type="text" name="quantity" onInput={quantityChange}></input>
                </div>
                <button type="submit">Add</button>
            </form>
        </div>
    );
}

export async function handleSubmit({ name = "filler", price = ""}) {

    var myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");

    var raw = JSON.stringify({
        "name": name,
    });

    var requestOptions = {
        method: 'POST',
        headers: myHeaders,
        body: raw,
        redirect: 'follow'
    };

    fetch("http://localhost:3001/stores/new", requestOptions)
        .then(response => response.text())
        .then(result => console.log(result))
        .catch(error => console.log('error', error));
}
